import React, {useState} from 'react';
import './index.css';

function Game() {
  return (
    <div>
      <Board/>
    </div>
  );
}

// square
  // value (props)
  // onClick  function (props)
function Square(props) {
  return(

  <button className="square" onClick={props.onClick}>{props.value}</button>
  )
}

// Board

function Board() {
  // state
  // boardState
  const [boardSquares, setBoardSquares] = useState(Array(9).fill(null));
    // turnState
  const [xIsNext, setXIsNext] = useState(true);
  // handleClick
  const handleCLick = index => {
    // copy of our board state
    const squares = [...boardSquares];
    // if the index of the board is filled, return
    if (squares[index]) return;
    // mutate that copy, and add X or O
    squares[index] = xIsNext ? "X" : "O"
    // calculate next turn

    // set the state of the board
    setBoardSquares(squares)
    // set the state of the turn
    setXIsNext(!xIsNext);
  };
  // creat our Board

  // creat a render square function
  const renderSquares = index => {
    return <Square value={boardSquares[index]} onClick={() => handleCLick(index)}/>
  }
    //  take in an index
      // return a square, with the correct value and function
  return(
  <div>
    <div>{renderSquares(0)}{renderSquares(1)}{renderSquares(2)}</div>
    <div>{renderSquares(3)}{renderSquares(4)}{renderSquares(5)}</div>
    <div>{renderSquares(6)}{renderSquares(7)}{renderSquares(8)}</div>
  </div>
  )
}

  // function that calculates the winner

export default Game;
